package com.tns;

public class Nested_if {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=100,b=20,c=40;
		if(a>b)
		{
			if(a>c)
			{
				
			System.out.println(a);
		}
			else
			{
				if(c<b)
				
				{
					System.out.println(c);
				}
				else
				{
					System.out.println(b);
				}
	    
			}
}
	
	}

}
